    };

    fetchQuestionsChapter2();
  }, [questions]); // Depends on questions to ensure proper ordering

  // Încărcăm întrebările salvate din localStorage
  useEffect(() => {
    const savedBookmarks = localStorage.getItem('bookmarkedQuestions');
    if (savedBookmarks) {
      try {
        const bookmarks = JSON.parse(savedBookmarks);
        setBookmarkedQuestions(new Set(bookmarks));
        setStats(prev => ({
          ...prev,
          bookmarkedCount: bookmarks.length
        }));
      } catch (e) {
        console.error('Eroare la încărcarea întrebărilor salvate:', e);
      }
    }
  }, []);

  // Actualizăm numărul de întrebări salvate când se modifică bookmarkedQuestions
  useEffect(() => {
    setStats(prev => ({
      ...prev,
      bookmarkedCount: bookmarkedQuestions.size
    }));
    
    // Salvăm întrebările marcate în localStorage
    localStorage.setItem('bookmarkedQuestions', JSON.stringify([...bookmarkedQuestions]));
  }, [bookmarkedQuestions]);

  // Funcția pentru schimbarea capitolului activ
  const switchChapter = (chapter) => {
    setActiveChapter(chapter);
    
    // Update filtered questions based on active chapter
    if (chapter === 'cap1') {
      setFilteredQuestions(questions);
    } else {
      setFilteredQuestions(questionsChapter2);
    }
  };

  // Funcția de căutare în întrebări
  const searchQuestions = (query) => {
    if (!query.trim()) {
      // Reset based on active chapter
      if (activeChapter === 'cap1') {
        setFilteredQuestions([...questions]);
      } else {
        setFilteredQuestions([...questionsChapter2]);
      }
      return true;
    }
    
    query = query.toLowerCase();
    
    // Select the appropriate question set based on active chapter
    const currentQuestions = activeChapter === 'cap1' ? questions : questionsChapter2;
    
    const filtered = currentQuestions.filter(q => {
      // Căutăm după numărul întrebării
      if (q.numar.toString() === query) {
        return true;
      }
      
      // Căutăm în textul întrebării
      if (q.intrebare.toLowerCase().includes(query)) {
        return true;
      }
      
      // Căutăm în variante
      for (const text of Object.values(q.variante)) {
        if (text.toLowerCase().includes(query)) {
          return true;
        }
      }
      
      return false;
    });
    
    setFilteredQuestions(filtered);
    
    return filtered.length > 0;
  };

  // Funcția pentru actualizarea statisticilor
  const updateStats = (isCorrect) => {
    setStats(prev => ({
      ...prev,
      correctCount: isCorrect ? prev.correctCount + 1 : prev.correctCount,
      totalCount: prev.totalCount + 1
    }));
  };

  // Funcția pentru resetarea statisticilor
  const resetStats = () => {
    setStats(prev => ({
      ...prev,
      correctCount: 0,
      totalCount: 0
    }));
  };

  // Funcția pentru toggle bookmark
  const toggleBookmark = (questionNumber) => {
    setBookmarkedQuestions(prev => {
      const newBookmarks = new Set(prev);
      if (newBookmarks.has(questionNumber)) {
        newBookmarks.delete(questionNumber);
      } else {
        newBookmarks.add(questionNumber);
      }
      return newBookmarks;
    });
  };

  if (isLoading) {
    return <LoadingOverlay />;
  }

  if (error) {
    return <div className="error-message">Eroare: {error}</div>;
  }

  // Get the correct answers based on active chapter
  const currentAnswers = activeChapter === 'cap1' ? correctAnswers : correctAnswersChapter2;

  return (
    <div className="App">
      <header className="App-header">
        <h1>Quiz Biologie</h1>
        <div className="chapter-selector">
          <button 
            className={`chapter-btn ${activeChapter === 'cap1' ? 'active' : ''}`}
            onClick={() => switchChapter('cap1')}
          >
            Capitolul 1
          </button>
          <button 
            className={`chapter-btn ${activeChapter === 'cap2' ? 'active' : ''}`}
            onClick={() => switchChapter('cap2')}
          >
            Capitolul 2
          </button>
        </div>
      </header>
      <main>
        <QuizContainer
          questions={activeChapter === 'cap1' ? questions : questionsChapter2}
          filteredQuestions={filteredQuestions}
          correctAnswers={currentAnswers}
          bookmarkedQuestions={bookmarkedQuestions}
          updateStats={updateStats}
          toggleBookmark={toggleBookmark}
          searchQuestions={searchQuestions}
        />
        <Statistics
          correctCount={stats.correctCount}
          totalCount={stats.totalCount}
          bookmarkedCount={stats.bookmarkedCount}
          resetStats={resetStats}
        />
      </main>
      <footer className="App-footer">
        <p>© 2023 Quiz Biologie</p>
      </footer>
    </div>
  );
}

export default App;
